const { statusCode, payload } = msg;

if ( statusCode !== 200 ) {
    throw `[ERROR] Status code ${statusCode}. ${payload}.`;
}

const html = payload;
const table = html.split("<table")[1];

const rows = table.match(/(<tr)(.*?)(<\/tr>)/gs);

for (let i = 0; i < rows.length; i++) {

    if (i > 0) { // to skip labels
        let values = rows[i].match( /(<td>)(.*?)(<\/td>)/gs ); // s = including new line in chars
        values = values.map( v => v.replace( "<td>", "" ).replace( "</td>", "" ).trim() );
