                throw `[ERROR] Response not matching reservoirs length (${values.length} vs ${reservoirs.length}).`;
            }

            const result = [];
            for ( let j = 0; j < values.length; j++ ) {
                result.push( {
                    date,
                    reservoir_id: reservoirs[ j ].id,
                    quantity: values[ j ]

                } );
            }
            msg.payload = result;
            return msg;
        }
    }
}

