const { statusCode, payload } = msg;

if ( statusCode !== 200 ) {
    throw `[ERROR] Status code ${statusCode}. ${payload}.`;
}

if ( payload.length !== locations.length ) {
    throw `[ERROR] Response not matching locations length (${payload.length} vs ${locations.length}).`;
}

const result = [];
for ( let i = 0; i < payload.length; i++ ) {
    const location = locations[ i ];
    const { time, 
        weather_code,
        temperature_2m_min,
        temperature_2m_mean,
        temperature_2m_max,
        precipitation_sum,
        rain_sum,
        snowfall_sum,
    } = payload[ i ].daily;

    for ( let j = 0; j < time.length; j++ ) {
        if ( time[ j ] == requestDate ) {
            if (
                temperature_2m_min[ j ] == undefined || temperature_2m_min[ j ] === null &&
                temperature_2m_mean[ j ] === undefined || temperature_2m_mean[ j ] === null &&
                temperature_2m_max[ j ] === undefined || temperature_2m_max[ j ] === null &&
                precipitation_sum[ j ] === undefined || precipitation_sum[ j ] === null
            ) {
                throw `[WARNING] Data not available yet (${requestDate}).`;
            }

            result.push( {
                date: requestDate,
                location_id: location.id,
                weather_code: weather_code[ j ],
                temperature_2m_min: temperature_2m_min[ j ],
                temperature_2m_mean:temperature_2m_mean[ j ],
                temperature_2m_max: temperature_2m_max[ j ],
                precipitation_sum: precipitation_sum[ j ],
                rain_sum: rain_sum[ i ],
                snowfall_sum: snowfall_sum[ i ]
            } );
            break;
        }
    }
    if ( result.length !== i + 1 ) {
        throw `[ERROR] No of the responded times matching requested date (${requestDate}).`;
    }
}

msg.payload = result;
return msg;

