const rows = [];
for ( let row of payload ) {
    const {
        date,
        location_id,
        weather_code,
        temperature_2m_min,
        temperature_2m_mean,
        temperature_2m_max,
        precipitation_sum,
        rain_sum,
        snowfall_sum
    } = row;

    rows.push( `("${date}",${location_id},${weather_code},${temperature_2m_min},${temperature_2m_mean},${temperature_2m_max},${precipitation_sum},${rain_sum},${snowfall_sum})` );
}

const values = rows.join( ',' );

const query = `INSERT INTO weather (date,location_id,weather_code,temperature_2m_min,temperature_2m_mean,temperature_2m_max,precipitation_sum,rain_sum,snowfall_sum) VALUES ${values}`;

msg.topic = query;
