    const clusters = makeClusters( values );
    // node.warn( clusters );
    const matchingClusterIndex = matchCluster( clusters, value );

    const levels = clusterLevels.map( x => x.name );
    levels[ matchingClusterIndex ] = levels[ matchingClusterIndex ].toUpperCase();
    const level = levels.join( ' ' );
    const color = clusterLevels[ matchingClusterIndex ].color; 
    const valueRepr = new Intl.NumberFormat().format( Math.trunc( value ) );

    results.push( { 
        name: reservoir.name_en,
        lat: reservoir.lat,
        lon: reservoir.lon,
        label: reservoir.name_en,
        iconColor: color, 
        period,
        average: `${valueRepr} cubic meters`,
        level 
    } );
}

